import React, { Component } from "react";
import { Table } from "react-bootstrap";

export default class List extends Component {
  state = {
    employee: [
      { name: "Alex", dep: "Tech", available: true },
      { name: "Bea", dep: "Project managment", available: true },
      { name: "Carl", dep: "HR", available: true },
      {
        name: "Dasha",
        dep: "Backend dev team",
        available: true
      },
      {
        name: "Eric",
        dep: "Business development",
        available: true
      },
      {
        name: "Francesca",
        dep: "Frontend dev team",
        available: true
      },
      {
        name: "Gabriella",
        dep: "Online marketing",
        available: true
      }
    ]
  };

  render() {
    let arr = ["Mon", "Tue", "Wed", "Thu", "Fri"];
    let arr2 = arr
      .slice(arr.indexOf(Date().substring(0, 3)))
      .concat(arr.slice(0, arr.indexOf(Date().substring(0, 3))));
    let available=this.state.employee.filter(x=>x.available==true);
    return (
      <div style={{ padding: "100px" }}>
        <Table striped bordered hover>
          <thead>
            <tr>
              <th style={{ width: "100px" }}>Day</th>
              <th style={{ width: "200px" }}>Open</th>
              <th style={{ width: "200px" }}>Close</th>
            </tr>
          </thead>
          <tbody>
            {arr2.map((x, i) =>
              i == 0 ? (
                <tr>
                  <td style={{ position: "relative" }}>
                    {" "}
                    <div
                      style={{
                        position: "absolute",
                        left: "0",
                        top: "10px",
                        padding: "0 4px",
                        color: "#fff",
                        backgroundColor: "red"
                      }}
                    >
                      Today
                    </div>
                    {x}
                  </td>
                  <td>
                    {available[0]
                      ? available[0].name / available[0].dep
                      : "No employee is available"}{" "}
                  </td>
                  <td>Otto</td>
                </tr>
              ) : (
                <tr>
                  <td>{arr2[1]}</td>
                  <td>Jacob</td>
                  <td>Thornton</td>
                </tr>
              )
            )}

            <tr>
              <td>{arr2[2]}</td>
              <td>Larry </td>
              <td>Larry </td>
            </tr>
            <tr>
              <td>{arr2[3]}</td>
              <td>Larry </td>
              <td>Larry </td>
            </tr>
            <tr>
              <td>{arr2[4]}</td>
              <td>Larry </td>
              <td>Larry </td>
            </tr>
          </tbody>
        </Table>
      </div>
    );
  }
}
