import React, { Component } from "react";
import { Table } from "react-bootstrap";

export default class List extends Component {
  state = {
    employee: [
      { name: "Alex", dep: "Tech", available: true },
      { name: "Bea", dep: "Project managment", available: true },
      { name: "Carl", dep: "HR", available: true },
      {
        name: "Dasha",
        dep: "Backend dev team",
        available: true
      },
      {
        name: "Eric",
        dep: "Business development",
        available: true
      },
      {
        name: "Francesca",
        dep: "Frontend dev team",
        available: true
      },
      {
        name: "Gabriella",
        dep: "Online marketing",
        available: true
      }
    ]
  };

  render() {
    let arr = ["Mon", "Tue", "Wed", "Thu", "Fri", "Sat", "Sun"];
    let arr2 = arr
      .slice(arr.indexOf(Date().substring(0, 3)))
      .concat(arr.slice(0, arr.indexOf(Date().substring(0, 3))));

    return (
      <div style={{ padding: "100px" }}>
        <Table striped bordered hover>
          <thead>
            <tr>
              <th style={{ width: "100px" }}>Day</th>
              <th style={{ width: "200px" }}>Open</th>
              <th style={{ width: "200px" }}>Close</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td style={{ position: "relative" }}>
                {" "}
                <div
                  style={{
                    position: "absolute",
                    left: "0",
                    top: "10px",
                    padding: "0 4px",
                    color: "#fff",
                    backgroundColor: "red"
                  }}
                >
                  Today
                </div>
                {arr2[0]}
              </td>
              <td>Mark</td>
              <td>Otto</td>
            </tr>
            <tr>
              <td>2</td>
              <td>Jacob</td>
              <td>Thornton</td>
            </tr>
            <tr>
              <td>3</td>
              <td>Larry </td>
              <td>Larry </td>
            </tr>
          </tbody>
        </Table>
      </div>
    );
  }
}
